class Solution(object):
    def isMatch(self, s, p):
        """
        :type s: str
        :type p: str
        :rtype: bool
        """
        
        dp = []

        sL = len(s)
        pL = len(p)

        nonStar = 0

        for j in p:
            if j != '*':
                nonStar += 1

        for i in range(pL+1):
            dp.append([False] * (sL + 1))




        if nonStar > sL:
            # we have more chars in pattern than string
            return False



        for i in range(pL+1):
            for j in range(sL+1):
                if i == 0 and j == 0:
                    # empty pattern and empty string
                    dp[i][j] = True
                    continue

                if i == 0 and j > 0:
                    # empty pattern and non-empty string
                    dp[i][j] = False
                    continue

                if p[i-1] != '*':
                    if j == 0:
                        # empty string with not '*' pattern
                        dp[i][j] = False
                    else:
                        dp[i][j] = dp[i-1][j-1]  and (p[i-1] == s[j-1] or p[i-1] == '?')

                else:
                    # zero match or one match or more than one match
                    if j == 0:
                        # if empty  string, only try zero match
                        dp[i][j] = dp[i-1][j]
                    else:
                        dp[i][j] = dp[i-1][j] or dp[i-1][j-1] or dp[i][j-1]

        rval =  dp[pL][sL]

        return rval


if __name__ == '__main__':
    s = Solution()
    s.isMatch("",
"**")